class Map:
    def __init__(self, game):
        self.game = game
        self.mini_map = mini_map
        self.world_map = {}
        # self.min_x = float('inf')
        # self.max_x = float('-inf')
        # self.min_y = float('inf')
        # self.max_y = float('-inf')
        self.get_map()
        self.fog_texture = (128, 128, 128)

    # def add_tile(self, x, y, texture):
    #     self.world_map[(x, y)] = texture
    #     self.min_x = min(self.min_x, x)
    #     self.max_x = max(self.max_x, x)
    #     self.min_y = min(self.min_y, y)
    #     self.max_y = max(self.max_y, y)

    # def is_out_of_bounds(self, x, y):
    #     return x < self.min_x or x > self.max_x or y < self.min_y or y > self.max_y

    def get_map(self):
        for j, row in enumerate(self.mini_map):
            for i, value in enumerate(row):
                if value:
                    self.world_map[(i, j)] = value

    def draw(self):
        [pg.draw.rect(self.game.screen, 'darkgrey', (pos[0] * 100, pos[1] * 100, 100, 100), 2)
            for pos in self.world_map]

